mapper.select:
select 

from tbl_brand
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}

mapper.delete:
delete from tbl_brand
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}

mapper.update:
update tbl_brand
set brand_code = #{brandCode,jdbcType=NCHAR},
  brand_name = #{brandName,jdbcType=NCHAR},
  customer_code_prefix = #{customerCodePrefix,jdbcType=NCHAR},
  indent_code_prefix = #{indentCodePrefix,jdbcType=NCHAR},
  m_code_prefix = #{mCodePrefix,jdbcType=NCHAR},
  remark = #{remark,jdbcType=NCHAR}
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}

