mapper.select:
select 

from tbl_vehicle_system
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}

mapper.delete:
delete from tbl_vehicle_system
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}

mapper.insert:
insert into tbl_vehicle_system (company_id, brand_id, Vehicle_system_id, 
  Vehicle_system_code, Vehicle_system_NAME, remark
  )
values (#{companyId,jdbcType=INTEGER}, #{brandId,jdbcType=INTEGER}, #{vehicleSystemId,jdbcType=INTEGER}, 
  #{vehicleSystemCode,jdbcType=NCHAR}, #{vehicleSystemName,jdbcType=NCHAR}, #{remark,jdbcType=NCHAR}
  )

mapper.update:
update tbl_vehicle_system


    Vehicle_system_code = #{vehicleSystemCode,jdbcType=NCHAR},


    Vehicle_system_NAME = #{vehicleSystemName,jdbcType=NCHAR},


    remark = #{remark,jdbcType=NCHAR},


where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}

update tbl_vehicle_system
set Vehicle_system_code = #{vehicleSystemCode,jdbcType=NCHAR},
  Vehicle_system_NAME = #{vehicleSystemName,jdbcType=NCHAR},
  remark = #{remark,jdbcType=NCHAR}
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}

