mapper.select:
select 

from tbl_vehicle_type_in_color
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}
  and Vehicle_type_id = #{vehicleTypeId,jdbcType=INTEGER}
  and Vehicle_type_in_color_id = #{vehicleTypeInColorId,jdbcType=INTEGER}

mapper.delete:
delete from tbl_vehicle_type_in_color
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}
  and Vehicle_type_id = #{vehicleTypeId,jdbcType=INTEGER}
  and Vehicle_type_in_color_id = #{vehicleTypeInColorId,jdbcType=INTEGER}

mapper.update:
update tbl_vehicle_type_in_color


    Vehicle_type_in_color_code = #{vehicleTypeInColorCode,jdbcType=NCHAR},


    Vehicle_type_in_color_name = #{vehicleTypeInColorName,jdbcType=NCHAR},


    remark = #{remark,jdbcType=NCHAR},


where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}
  and Vehicle_type_id = #{vehicleTypeId,jdbcType=INTEGER}
  and Vehicle_type_in_color_id = #{vehicleTypeInColorId,jdbcType=INTEGER}

update tbl_vehicle_type_in_color
set Vehicle_type_in_color_code = #{vehicleTypeInColorCode,jdbcType=NCHAR},
  Vehicle_type_in_color_name = #{vehicleTypeInColorName,jdbcType=NCHAR},
  remark = #{remark,jdbcType=NCHAR}
where company_id = #{companyId,jdbcType=INTEGER}
  and brand_id = #{brandId,jdbcType=INTEGER}
  and Vehicle_system_id = #{vehicleSystemId,jdbcType=INTEGER}
  and Vehicle_type_id = #{vehicleTypeId,jdbcType=INTEGER}
